package main

import (
	"fmt"
)

const (
	ESC string = "\x1b"

	Black         string = ESC + "[30m"
	BrightBlack   string = ESC + "[30;1m"
	Red           string = ESC + "[31m"
	BrightRed     string = ESC + "[31;1m"
	Green         string = ESC + "[32m"
	BrightGreen   string = ESC + "[32;1m"
	Yellow        string = ESC + "[33m"
	BrightYellow  string = ESC + "[33;1m"
	Blue          string = ESC + "[34m"
	BrightBlue    string = ESC + "[34;1m"
	Magenta       string = ESC + "[35m"
	BrightMagenta string = ESC + "[35;1m"
	Cyan          string = ESC + "[36m"
	BrightCyan    string = ESC + "[36;1m"
	White         string = ESC + "[37m"
	BrightWhite   string = ESC + "[37;1m"

	C256  string = ESC + "[38;5;%sm"
	BG256 string = ESC + "[48;5;%sm"

	Reset   string = ESC + "[0m"
	NoColor string = ""

	TopLeft      string = ESC + "[H"
	CLEAR_SCREEN string = ESC + "[2J"

	LF    string = "\r"
	Break string = LF + "\n"
)

func main() {
	clearScreen()
	m := mapGen()
	colors := map[string]string{
		"~": "20",
		"T": "94",
		"O": "242",
		"o": "241",
		"#": "22",
		"W": "70",
		"w": "71",
		".": "234",
		":": "64",
		"$": "10",
		"|": "130",
		"@": "232",
		"+": "243",
		"0": "240",
		"-": "130",
	}

	for y := range m.h {
		for x := range m.w {
			index := y*m.w + x
			c := m.char[index]
			drawAt(x+1, y+1, colored(
				colored(
					c, color256(C256, colors[c]),
				),
				color256(BG256, colors[c]),
			))
		}
	}

	drawAt(0, 50, Break)
}

func clearScreen() {
	fmt.Print(CLEAR_SCREEN, TopLeft)
}

func drawAt(x, y int, char string) {
	fmt.Printf("%s[%d;%dH%s", ESC, y, x, char)
}

func colored(char, color string) string {
	if len(color) == 0 {
		return string(char)
	}
	return fmt.Sprintf("%s%s%s", color, char, Reset)
}

func color256(code, color string) string {
	if len(code) == 0 || len(color) == 0 {
		return ""
	}
	return fmt.Sprintf(code, color)
}

func absXYfromCenter(x, y, offset_x, offset_y, w, h int) (int, int) {
	return (offset_x - (w / 2)) + x, (offset_y - (h / 2)) + y
}

